java@Configuration
@EnableConfigurationProperties(RedissonLockProperties.class)
public class RedissonLockConfig &#123;

    @Bean(destroyMethod = &quot;shutdown&quot;)
    public RedissonClient redissonClient(RedissonLockProperties properties) &#123;
        Config config = new Config();
        switch (properties.getModel()) &#123;
            case SENTINEL:
                RedissonLockProperties.RSentinel sentinelProps = properties.getSentinel();
                SentinelServersConfig sentinelServersConfig = config.useSentinelServers();
                sentinelServersConfig.setMasterName(sentinelProps.getMaster());
                List&lt;String&gt; addresses = sentinelProps.getAddresses().stream()
                        .map(e -&gt; RedissonLockProperties.REDIS_PROTO_PREFIX + e)
                        .collect(Collectors.toList());
                sentinelServersConfig.setSentinelAddresses(addresses);
                sentinelServersConfig.setScanInterval(sentinelProps.getScanInterval());
                Optional.ofNullable(sentinelProps.getPassword())
                        .ifPresent(sentinelServersConfig::setPassword);
                break;
            case CLUSTER:
                ClusterServersConfig clusterServersConfig = config.useClusterServers();
                RedissonLockProperties.RCluster clusterProps = properties.getCluster();
                List&lt;String&gt; addr = clusterProps.getAddresses().stream()
                        .map(e -&gt; RedissonLockProperties.REDIS_PROTO_PREFIX + e)
                        .collect(Collectors.toList());
                clusterServersConfig.setNodeAddresses(addr);
                clusterServersConfig.setScanInterval(clusterProps.getScanInterval());
                Optional.ofNullable(clusterProps.getPassword())
                       .ifPresent(clusterServersConfig::setPassword);
                break;
            default:
                RedissonLockProperties.RSingleton singletonProps = properties.getSingleton();
                String address = RedissonLockProperties.REDIS_PROTO_PREFIX + singletonProps.getAddress();
                SingleServerConfig singleServerConfig = config.useSingleServer()
                        .setTimeout(singletonProps.getTimeout())
                        .setPingConnectionInterval(singletonProps.getPingConnectionInterval())
                        .setConnectionPoolSize(singletonProps.getConnectionPoolSize())
                        .setConnectionMinimumIdleSize(singletonProps.getConnectionMinimumIdleSize())
                        .setAddress(address).setDatabase(0);
                Optional.ofNullable(singletonProps.getPassword())
                        .ifPresent(singleServerConfig::setPassword);
                break;
        &#125;
        return Redisson.create(config);
    &#125;
