VALUES (&#39;order_lock&#39;, &#39;init&#39;, &#39;1970-01-01&#39;);锁实现java@Component
public class DatabaseLock &#123;

    @Autowired
    private JdbcTemplate jdbcTemplate;

    private ThreadLocal&lt;String&gt; holderId = new ThreadLocal&lt;&gt;(); // 实例唯一标识

    /**
     * 尝试获取分布式锁
     * @param lockKey 锁名称
     * @param expireSeconds 锁过期时间(秒)
     * @param retryTimes 重试次数
     * @param retryInterval 重试间隔(毫秒)
     */
    public boolean tryLock(String lockKey, int expireSeconds, int retryTimes, long retryInterval) &#123;
        for (int i = 0; i &lt;= retryTimes; i++) &#123;
            if (acquireLock(lockKey, expireSeconds)) &#123;
                return true;
            &#125;
            try &#123;
                Thread.sleep(retryInterval);
            &#125; catch (InterruptedException e) &#123;
                Thread.currentThread().interrupt();
            &#125;
        &#125;
        return false;
    &#125;

    private boolean acquireLock(String lockKey, int expireSeconds) &#123;
        holderId.set(UUID.randomUUID().toString());
        LocalDateTime now = LocalDateTime.now();
        LocalDateTime expireTime = now.plusSeconds(expireSeconds);

        // 1. 清理过期锁
        jdbcTemplate.update(
            &quot;DELETE FROM distributed_lock WHERE lock_key = ? AND expire_time &lt; ?&quot;,
            lockKey, now
        );

        // 2. 尝试插入新锁记录
        try &#123;
            jdbcTemplate.update(
                &quot;INSERT INTO distributed_lock(lock_key, holder_id, expire_time) VALUES (?, ?, ?)&quot;,
                lockKey, holderId.get(), expireTime
            );
            return true;
        &#125; catch (DuplicateKeyException e) &#123;
            // 3. 已有锁存在时尝试抢占
            int updated = jdbcTemplate.update(
                &quot;UPDATE distributed_lock SET holder_id = ?, expire_time = ?, version = version + 1 &quot; +
                &quot;WHERE lock_key = ? AND (holder_id = ? OR expire_time &lt; ?)&quot;,
                holderId.get(), expireTime, lockKey, holderId.get(), now
            );
            return updated &gt; 0;
        &#125;
    &#125;

    /**
     * 释放分布式锁
     */
    public void releaseLock(String lockKey) &#123;
        jdbcTemplate.update(
            &quot;DELETE FROM distributed_lock WHERE lock_key = ? AND holder_id = ?&quot;,
            lockKey, holderId
        );
        holderId.remove();
    &#125;
